function parseDatetime(datetime) {
    var tabDateTime = datetime.split(" "),
            tabDate = tabDateTime[0].split("-"),
            tabTime = tabDateTime[1].split(":"),
            year = tabDate[2],
            month = parseInt(tabDate[1], 10) - 1,
            day = parseInt(tabDate[0], 10),
            h = parseInt(tabTime[0], 10),
            m = parseInt(tabTime[1], 10),
            s = 0;
    return new Date(year, month, day, h, m, s);

}
