function setCalendar(idsel, idfrom, idto) {
    var sel = $('#' + idsel + " option:selected").val();
    d = new Date();
    if (sel == '') {//Aujourd'hui
        $('#' + idfrom).val('');
        $('#' + idto).val('');
    } else if (sel == 1) {//Aujourd'hui
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date()));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date()));
    } else if (sel == 2) {//Hier
        //alert(1);
        s = parseInt(d.getTime()) - 86400000;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date(s)));
    } else if (sel == 3) {//Avant-Hier
        s = parseInt(d.getTime()) - 86400000 * 2;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date(s)));
    } else if (sel == 4) {//Depuis Hier
        s = parseInt(d.getTime()) - 86400000;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date()));
    }
    else if (sel == 5) {////Depuis Avant-Hier
        s = parseInt(d.getTime()) - 86400000 * 2;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date()));
    } else if (sel == 6) {//Depuis une semaine

        s = parseInt(d.getTime()) - 86400000 * 7;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date()));
    } else if (sel == 7) {//semaine courante
        /*<?php $tmp = get_lundi_dimanche_from_week((date('W')), date('Y'));?>*/
        $('#' + idfrom).val($.PHPDate('d-m-Y' /*<?php echo ','.$tmp[0]; ?>*/));
        $('#' + idto).val($.PHPDate('d-m-Y'/*<?php echo ','.$tmp[1]; ?>*/));
    } else if (sel == 8) {//Semaine passée       
        s = parseInt(d.getTime()) - 86400000 * 7;
        $('#' + idfrom).val($.PHPDate('d-m-Y', new Date(s)));
        $('#' + idto).val($.PHPDate('d-m-Y', new Date()));
    }
}

