function isDateTime(dt) {
    var regex = /^((((((0[1-9])|(1\d)|(2[0-8]))-((0[1-9])|(1[0-2])))|((31-((0[13578])|(1[02])))|((29|30)-((0[1,3-9])|(1[0-2])))))-((20[0-9][0-9]))|(29-02-20(([02468][048])|([13579][26])))))$|^((((((0[1-9])|(1\d)|(2[0-8]))-((0[1-9])|(1[0-2])))|((31-((0[13578])|(1[02])))|((29|30)-((0[1,3-9])|(1[0-2])))))-((20[0-9][0-9]))|(29-02-20(([02468][048])|([13579][26]))))\s(([1-9]{1})|([0-1][0-9])|([1-2][0-3])):([0-5][0-9]))$/;
    return regex.test(dt);
}
function createdateinputs(idfrom, idfhour, idto, idthour) {
    $("#" + idfrom + ", #" + idto).each(function () {
        $(this).val($.PHPDate('d-m-Y', new Date()));
    });
    var dates = $("#" + idfrom + ", #" + idto).datepicker({
        defaultDate: +0,
        changeMonth: true,
        numberOfMonths: 1,
        dateFormat: 'dd-mm-yy',
        showOn: "button",
        buttonImage: 'images/calendar.gif',
        buttonImageOnly: true,
        showAnim: 'slideDown',
        beforeShow: function (input, inst) {
            dates.not(inst).datepicker("option", "maxDate", new Date());
        },
        onSelect: function (selectedDate) {
            var option = this.id == idfrom ? "minDate" : "maxDate",
                    instance = $(this).data("datepicker"),
                    date = $.datepicker.parseDate(
                            instance.settings.dateFormat ||
                            $.datepicker._defaults.dateFormat,
                            selectedDate, instance.settings);
            dates.not(this).datepicker("option", option, date);
            if (this.id == idfrom) {
                $("#" + idfhour).val('00:00');
            } else if (this.id == idto) {
                $("#" + idthour).val('23:59');
            }

        }
    });

    var times = $("#" + idfhour + ", #" + idthour).timepicker({
        addSliderAccess: true,
        sliderAccessArgs: {
            touchonly: false
        }
    });
}

function validatedateinputs(idfrom, idfhour, idto, idthour) {

    if ($("#" + idto).val() == undefined) {
        if ($("#" + idfrom).val().length == 0) {
            alert("N'oubliez pas d'entrer les dates!");
            $(this).focus();
            return false;
        }
        var fhour = $("#" + idfhour).val().length != 0 ? $("#" + idfhour).val().trim() : '00:00';
        var fromObj = $("#" + idfrom);
        var from = fromObj.val().trim() + ' ' + fhour;

        if (!isDateTime(from)) {
            alert('Format de date ou heure invalide (date: dd-mm-aaaa|heure: hh:mm)');
            fromObj.focus();
            return false;
        }
        return true;
    }

    $("#" + idfrom + ", #" + idto).each(function () {
        if ($(this).val().length == 0) {
            alert("N'oubliez pas d'entrer les dates!");
            $(this).focus();
            return false;
        }
    });//fin each
    var fhour = $("#" + idfhour).val().length != 0 ? $("#" + idfhour).val().trim() : '00:00',
            thour = $("#" + idthour).val().length != 0 ? $("#" + idthour).val().trim() : '23:59';
    var fromObj = $("#" + idfrom),
            toObj = $("#" + idto);
    var from = fromObj.val().trim() + ' ' + fhour,
            to = toObj.val().trim() + ' ' + thour;
    if (!isDateTime(from) || !isDateTime(to)) {
        alert('Format de date ou heure invalide (date: dd-mm-aaaa|heure: hh:mm)');
        fromObj.focus();
        return false;
    }
    else if (Date.parse(from, 'd-M-yyyy HH:mm').compareTo(Date.parse(to, 'd-M-yyyy HH:mm')) == 1) {
        alert('Intervalle dates/heures invalide');
        fromObj.focus();
        return false;
    }
    else if (Date.today().set({
        hour: 23,
        minute: 59,
        second: 59
    }).compareTo(Date.parse(to, 'd-M-yyyy HH:mm')) == -1) {
        alert("La période ne peut pas dépasser la date d'aujourd'hui!");
        toObj.val($.PHPDate('d-m-Y', new Date()));
        toObj.focus();
        return false;
    }
    return true;
}



