/**
 * Affiche toutes les zones de controle sur la carte;
 *
 */
function show_zones_on_map(carte, level1, level2, init) {

    if (typeof (level1) == 'undefined')
        level1 == 'all';

    if (typeof (level2) == 'undefined')
        level2 == 'all';

    if (typeof (init) == 'undefined')
        init = 0;
    //console.log(level1);
    geoZoneInfoWin = new google.maps.InfoWindow({
        content: ""
    });

    function show_zones(zObject, carte, level1, level2) {

        var polyOpt = {
            map: carte.getGMap(),
            draggable: false,
            editable: false,
            clickable: true,
            fillColor: '#666',
            fillOpacity: 0.5,
            strokeWeight: 1
        };

        google.maps.event.addListener(carte.getGMap(), 'click', function () {
            geoZoneInfoWin.close();
        });

        if (carte.getGeoZonesDisplayed()) {
            if (carte.zonesJson.length > 0) {
                for (var i = 0; i < carte.zonesJson.length; i++) {
                    if (carte.zonesJson[i] !== undefined) {
                        carte.zonesJson[i].setMap(null);
                    }
                }
            }
            carte.setGeoZonesDisplayed(false);
        } else {


            for (var i = 0; i < zObject.zones_bounds.length; i++) {
                if (level1 == 'all') {//afficher toutes les zones
                    var target_bounds = zObject.zones_bounds[i];
                    polyOpt.name = zObject.zones_names[i];
                    carte.zonesJson[i] = new GeoJSON(eval('(' + target_bounds + ')'), polyOpt);

                    google.maps.event.addListener(carte.zonesJson[i], 'click', function (event) {
                        var contentString = "<div style='font-weight : 500'>" + this.name + "</div>" // + event.latLng.toUrlValue(6);
                        geoZoneInfoWin.setContent(contentString);
                        geoZoneInfoWin.setPosition(event.latLng);
                        geoZoneInfoWin.open(carte.getGMap());
                    });

                } else if (level2 == 'all') {//afficher uniquement les zones appartenant à la catégorie choisie

                    if (zObject.idlevel1[i] == level1) {
                        var target_bounds = zObject.zones_bounds[i];
                        polyOpt.name = zObject.zones_names[i];
                        carte.zonesJson[i] = new GeoJSON(eval('(' + target_bounds + ')'), polyOpt);

                        google.maps.event.addListener(carte.zonesJson[i], 'click', function (event) {
                            var contentString = "<div style='font-weight : 500'>" + this.name + "</div>" // + event.latLng.toUrlValue(6);
                            geoZoneInfoWin.setContent(contentString);
                            geoZoneInfoWin.setPosition(event.latLng);
                            geoZoneInfoWin.open(carte.getGMap());
                        });
                    }

                } else { //afficher uniquement les zones appartenant au sous catégorie choisi
                    if (zObject.idlevel2[i] == level2) {
                        var target_bounds = zObject.zones_bounds[i];
                        polyOpt.name = zObject.zones_names[i];
                        carte.zonesJson[i] = new GeoJSON(eval('(' + target_bounds + ')'), polyOpt);

                        google.maps.event.addListener(carte.zonesJson[i], 'click', function (event) {
                            var contentString = "<div style='font-weight : 500'>" + this.name + "</div>" // + event.latLng.toUrlValue(6);
                            geoZoneInfoWin.setContent(contentString);
                            geoZoneInfoWin.setPosition(event.latLng);
                            geoZoneInfoWin.open(carte.getGMap());
                        });
                    }
                }

            }

            carte.setGeoZonesDisplayed(true);
        }
    }

    if (geoZones === null) {
        $.mobile.loading("show");
        $.ajax({
            type: "POST",
            url: "mapGeoZones.php",
            data: {csrf_token: at_token}
        }).done(function (data) {
            geoZones = jsonToObject(data);// JSON string TO object
            if (init == 0) {
                show_zones(geoZones, carte, level1, level2);
            }

            $.mobile.loading("hide");

        }).fail(function (jqXHR, textStatus) {
            $("#gteditform div#messageBox").html("Request failed: " + textStatus + "<br />");
        });
    } else {
        show_zones(geoZones, carte, level1, level2);
    }
}

